import React, { useState } from 'react';
import Square from './Square';
import './board.css'

function Board() {
    const [squareval,setsquareval] = useState(Array(9).fill(null));
    const [isxturn,setisxturn] = useState(true);
    const [msg,setmsg] = useState('Take your turn #1')
    const handleclick = (idx)=>{ 
        if(squareval[idx]!=null) return;
        const newarr = [...squareval];
        newarr[idx]=(isxturn)?'X':'0';
        const valput = (isxturn)?'X':'0';
        const wincount = (isxturn)?'1':'2';
        if(checkwin(newarr,valput)){
          setmsg(valput+' WONNNN!!!');
        } else {
          const nxtturn = (isxturn)?'2':'1';
          setisxturn(!isxturn);
          setmsg('Take your turn ',nxtturn);
        }
    }
    const checkwin = (newarr, valput)=>{
      if(newarr[0]==newarr[4] && newarr[0]==newarr[8]) return true;
      if(newarr[0]==newarr[3] && newarr[0]==newarr[6]) return true;
      if(newarr[0]==newarr[1] && newarr[0]==newarr[2]) return true;
      if(newarr[1]==newarr[4] && newarr[1]==newarr[7]) return true;
      if(newarr[2]==newarr[5] && newarr[2]==newarr[8]) return true;
      if(newarr[3]==newarr[4] && newarr[3]==newarr[5]) return true;
      if(newarr[6]==newarr[7] && newarr[6]==newarr[8]) return true;
      if(newarr[6]==newarr[4] && newarr[6]==newarr[2]) return true;
      return false;
    }

  return (
    <>
      <div className='full-view'>
          <h1>{msg}</h1>
          <div className='board-nav'>
              <div className='board'>
                  <Square value={squareval[0]} squareclick={handleclick(0)}/>
                  <Square value={squareval[1]} squareclick={handleclick(1)}/>
                  <Square value={squareval[2]} squareclick={handleclick(1)}/>

                  <Square value={squareval[3]} squareclick={handleclick(1)}/>
                  <Square value={squareval[4]} squareclick={handleclick(1)}/>
                  <Square value={squareval[5]}  squareclick={handleclick(1)}/>

                  <Square value={squareval[6]}/>
                  <Square value={squareval[7]}/>
                  <Square value={squareval[8]}/>
              </div>
              <div className='navigation'>
                     rfjrfrrfr
              </div>
          </div>  
      </div> 
    </>
  )
}

export default Board;
